import React, {useState, useEffect} from 'react';
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBInput,
  MDBCheckbox,
  MDBIcon
}
from 'mdb-react-ui-kit';
import './app.css';


function App() {
  const [allCheck, setAllCheck] = useState(false);
  const [ageCheck, setAgeCheck] = useState(false);
  const [useCheck, setUseCheck] = useState(false);
  const [marketingCheck, setMarketingCheck] = useState(false);

  const allBtnEvent =()=>{
    if(allCheck === false) {
      setAllCheck(true);
      setAgeCheck(true);
      setUseCheck(true);
      setMarketingCheck(true);
    }else {
      setAllCheck(false);
      setAgeCheck(false);
      setUseCheck(false);
      setMarketingCheck(false);
    } 
  };

  const ageBtnEvent =()=>{
    if(ageCheck === false) {
      setAgeCheck(true)
    }else {
      setAgeCheck(false)
    }
  };

  const useBtnEvent =()=>{
    if(useCheck === false) {
      setUseCheck(true)
    }else {
      setUseCheck(false)
    }
  };

  const marketingBtnEvent =()=>{
    if(marketingCheck === false) {
      setMarketingCheck(true)
    }else {
      setMarketingCheck(false)
    }
  };

  useEffect(()=>{
    if(ageCheck===true && useCheck===true && marketingCheck===true){
      setAllCheck(true)
    } else {
      setAllCheck(false)
    }
  }, [ageCheck,useCheck, marketingCheck])



  return (
    <MDBContainer fluid className='p-4 background-radial-gradient overflow-hidden'>

      <MDBRow>

        <MDBCol md='6' className='text-center text-md-start d-flex flex-column justify-content-center'>

          <h1 className="my-5 display-3 fw-bold ls-tight px-3" style={{color: 'hsl(218, 81%, 95%)'}}>
            Kaimind <br />
            <span style={{color: 'hsl(218, 81%, 75%)'}}>친구들과 시작해보세요!</span>
          </h1>

          <p className='px-3' style={{color: 'hsl(218, 81%, 85%)'}}>

          </p>

        </MDBCol>

        <MDBCol md='6' className='position-relative'>

          <div id="radius-shape-1" className="position-absolute rounded-circle shadow-5-strong"></div>
          <div id="radius-shape-2" className="position-absolute shadow-5-strong"></div>

          <MDBCard className='my-5 bg-glass'>
            <MDBCardBody className='p-5'>

              <MDBRow>
                <MDBCol col='6'>
                  <MDBInput wrapperClass='mb-4' label='First name' id='form1' type='text'/>
                </MDBCol>

                <MDBCol col='6'>
                  <MDBInput wrapperClass='mb-4' label='Last name' id='form2' type='text'/>
                </MDBCol>
              </MDBRow>

              <MDBInput wrapperClass='mb-4' label='Email' id='form3' type='email'/>
              <MDBInput wrapperClass='mb-4' label='Password' id='form4' type='password'/>

              <form method="post" action="" className={styles.form}>
                <div className={styles.form_agreement}>
                  <label className={styles.form_agreement_title}>
                    약관동의
                  </label>
                  <div className={styles.form_agreement_box}>
                    <div className={styles.form_agreement_all}>
                      <input type="checkbox" id="all-check" checked={allCheck} onChange={allBtnEvent}/>
                      <label for="all-check">전체동의</label>
                    </div>
                    <div className={styles.form_agreement_item}>
                      <input type="checkbox" id="check1" checked={ageCheck} onChange={ageBtnEvent}/>
                      <label for="check1">만 14세 이상입니다 <span className={styles.blue}>(필수)</span></label>
                    </div>
                    <div className={styles.form_agreement_item}>
                      <input type="checkbox" id="check2" checked={useCheck}  onChange={useBtnEvent}/>
                      <label for="check2">이용약관 <span className={styles.blue}>(필수)</span></label>
                    </div>
                    <div className={styles.form_agreement_item}>
                      <input type="checkbox" id="check3" checked={marketingCheck}  onChange={marketingBtnEvent}/>
                      <label for="check3">마케팅 동의 <span className={styles.gray}>(선택)</span></label>
                    </div>
                  </div>
                </div>
                <button type="submit" className={`${styles.btn_primary} ${styles.btn_55} ${styles.btn_submit}`}>
                  회원가입하기
                </button>
              </form>

              <MDBBtn className='w-100 mb-4' size='md'>회원가입</MDBBtn>

              <div className="text-center">

                <p>다음 계정으로 가입하기</p>

                <MDBBtn tag='a' color='none' className='mx-3' style={{ color: '#1266f1' }}>
                  <img src="/img/kakao.png" alt="Kakao" width="30" height="30" />
                </MDBBtn>
              </div>

            </MDBCardBody>
          </MDBCard>

        </MDBCol>

      </MDBRow>

    </MDBContainer>
  );
}

export default App;
