import React, { useEffect, useRef, useState } from 'react';
import { fabric } from 'fabric';

const colorPalette = ['#000000', '#FF0000', '#00FF00', '#0000FF', '#FFFF00', '#FF00FF', '#00FFFF', '#FFFFFF'];

const Drawboard = () => {
  const canvasRef = useRef(null);
  const fabricCanvasRef = useRef(null);
  const drawingToolRef = useRef(null);
  const [isErasing, setIsErasing] = useState(false);
  const [currentColor, setCurrentColor] = useState(colorPalette[0]);

  useEffect(() => {
    const canvas = new fabric.Canvas(canvasRef.current, {
      width: 500,
      height: 500,
      backgroundColor: '#ffffff', // Set your desired canvas background color
    });

    fabricCanvasRef.current = canvas;

    return () => {
      // Clean up the Fabric.js canvas instance on unmount
      canvas.dispose();
    };
  }, []);

  const onMouseDown = (event) => {
    if (drawingToolRef.current && drawingToolRef.current.onMouseDown) {
      drawingToolRef.current.onMouseDown(event.e);
    }
  };

  const onMouseMove = (event) => {
    if (drawingToolRef.current && drawingToolRef.current.onMouseMove) {
      drawingToolRef.current.onMouseMove(event.e);
    }
  };

  const onMouseUp = (event) => {
    if (drawingToolRef.current && drawingToolRef.current.onMouseUp) {
      drawingToolRef.current.onMouseUp(event.e);
    }
  };

  useEffect(() => {
    const canvas = fabricCanvasRef.current;

    // Event handlers for mouse events
    canvas.on('mouse:down', onMouseDown);
    canvas.on('mouse:move', onMouseMove);
    canvas.on('mouse:up', onMouseUp);

    return () => {
      // Remove the event listeners on unmount
      canvas.off('mouse:down', onMouseDown);
      canvas.off('mouse:move', onMouseMove);
      canvas.off('mouse:up', onMouseUp);
    };
  }, []);

  const setDrawingTool = (tool) => {
    drawingToolRef.current = tool;
  };

  const enableFreeDrawing = () => {
    const canvas = fabricCanvasRef.current;

    canvas.isDrawingMode = true;
    canvas.freeDrawingBrush.width = 2; // Set the brush width as needed

    if (isErasing) {
      canvas.freeDrawingBrush.color = '#ffffff'; // Set the brush color to white for erasing
    } else {
      canvas.freeDrawingBrush.color = currentColor; // Set the brush color to the selected color
    }
  };

  const handleColorChange = (color) => {
    setCurrentColor(color);
    setIsErasing(false); // Disable eraser when a color is selected
  };

  const toggleEraser = () => {
    setIsErasing((prev) => !prev);
  };

  return (
    <div>
      <div>
        <canvas
          ref={canvasRef}
          style={{
            width: '500px',
            height: '500px',
            border: '1px solid #ccc',
            /* Add any other desired styles for the canvas here */
          }}
        />
      </div>
      <div>
        <label>Select Color: </label>
        {colorPalette.map((color) => (
          <button
            key={color}
            style={{ backgroundColor: color, width: '30px', height: '30px', margin: '5px' }}
            onClick={() => handleColorChange(color)}
          />
        ))}
        <button onClick={enableFreeDrawing}>Enable Drawing</button>
        <button onClick={toggleEraser}>{isErasing ? 'Disable Eraser' : 'Enable Eraser'}</button>
      </div>
    </div>
  );
};

export default Drawboard;
